package practice;

public class Ex_ForLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 5;i >=1;i--) {   //1 = 5
			 
			for (int j = 1 ;j <= i;j ++) {  //12345  1234  123 12 1
				System.out.print(j+" ");
				
			}
			System.out.println();
		}

	}

}
